package item;import java.util.List;import java.util.ArrayList;public class CoffeeTruck {    private double capacity;    private List<CoffeeItem> items;    public CoffeeTruck(double capacity) {        this.capacity = capacity;        this.items = new ArrayList<>();    }    public void loadCoffee(String type, String condition, double weight, double price) {        CoffeeItem item = new CoffeeItem(type, condition, price, weight);        if (getTotalWeight() + item.getWeight() <= capacity) {            items.add(item);            System.out.println("Coffee loaded successfully.");        } else {            System.out.println("Not enough space.");        }    }    @Override    public String toString() {        StringBuilder result = new StringBuilder("\n");        for (CoffeeItem item : items) {            result.append(item.toString()).append("\n");        }        return result.toString();    }    public double getTotalWeight() {        double totalWeight = 0;        for (CoffeeItem item : items) {            totalWeight += item.getWeight();        }        return totalWeight;    }    public List<CoffeeItem> getItems() {        return items;    }}